.btn {
  cursor: pointer;
  font-size: var(--fs-100);
  font-weight: 700;
  letter-spacing: 2px;
  text-decoration: none;
  text-transform: uppercase;
  color: inherit;
  display: inline-block;
  border: none;
  background-color: transparent;
}
.btn--box {
  padding: 0.9em 2em;
  transition: background-color 400ms, color 400ms;
  background-color: black;
  color: white;
}
.dark .btn--box {
  background-color: white;
  color: black;
}
.btn--box:hover, .btn--box:focus {
  background-color: rgba(0, 0, 0, 0.15);
  color: black;
}
.dark .btn--box:hover, .dark .btn--box:focus {
  background-color: rgba(255, 255, 255, 0.7);
  color: white;
}
.btn--arrow::after {
  content: "";
  display: inline-block;
  line-height: 2em;
  width: 50px;
  height: 1em;
  margin-left: 1.6em;
  transition: margin-left 250ms;
  background-image: url("/5b12eb74bda8283a0aaced07d23b4607.svg");
  background-repeat: no-repeat;
}
.dark .btn--arrow::after {
  background-image: url("/e29aa75f5727bf762fc6343606acb568.svg");
}
.btn--arrow:hover, .btn--arrow:focus {
  text-decoration: underline;
}
.btn--arrow:hover::after, .btn--arrow:focus::after {
  margin-left: 0.15em;
}
.btn--nav-toggle {
  position: relative;
  width: 25px;
  aspect-ratio: 1;
}
.btn--nav-toggle::before, .btn--nav-toggle::after {
  display: block;
  content: "";
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  background-repeat: no-repeat;
  background-position: center;
  transition: transform 250ms, opacity 250ms;
}
.btn--nav-toggle::before {
  background-image: url("/9efc1e05e03d3da45d8dad9554705f9d.svg");
}
.btn--nav-toggle::after {
  background-image: url("/42e292508957b7684f9c9cc507b83a3c.svg");
  opacity: 0;
  transform: rotate(90deg);
}
.btn--nav-toggle[aria-expanded=true]::before {
  transform: rotate(90deg);
  opacity: 0;
}
.btn--nav-toggle[aria-expanded=true]::after {
  transform: rotate(0deg);
  opacity: 1;
}

