sources.scss/abstracts/_mixins.scss:
@use "./maps" as *;
@use "./functions" as *;

// media
@mixin media($min, $max: null) {
  $min-width: 0px;
  $max-width: null;

  @if type-of($min) == "string" {
    $min-width: map-get($breakpoints, $min);
  } @else {
    $min-width: $min;
  }

  @if $max {
    @if type-of($max) == "string" {
      $max-width: map-get($breakpoints, $max);
    } @else {
      $max-width: $max;
    }

    @media (min-width: $min-width) and (max-width: $max-width) {
      @content;
    }
  } @else {
    @media (min-width: $min-width) {
      @content;
    }
  }
}

//spacing
@mixin spacing($s, $vs: $s) {
  .#{$s} {
    margin-bottom: var(--#{$vs}, 0);

    .col > & {
      margin-bottom: 0;
      margin-right: var(--#{$vs}, 0);
    }

    .col--small > & {
      @include media("small") {
        margin-bottom: 0;
        margin-right: var(--#{$vs}, 0);
      }
    }

    .col--medium > & {
      @include media("medium") {
        margin-bottom: 0;
        margin-right: var(--#{$vs}, 0);
      }
    }

    .col--large > & {
      @include media("large") {
        margin-bottom: 0;
        margin-right: var(--#{$vs}, 0);
      }
    }
  }
}

//with versions
@mixin withVersions($name) {
  .#{$name} {
    @content;

    &--small {
      @include media("small") {
        @content;
      }
    }

    &--medium {
      @include media("medium") {
        @content;
      }
    }

    &--large {
      @include media("large") {
        @content;
      }
    }
  }
}

// color-strip
@mixin color-strip($position, $child: "after") {
  $thickness: 5px;
  position: relative;

  &::#{$child} {
    content: "";
    position: absolute;

    @if $position == "bottom" {
      width: 100%;
      height: $thickness;
      top: 100%;
      left: 0;

      background-image: linear-gradient(
        to right,
        clr(accent1),
        clr(accent2),
        clr(accent3)
      );
    }

    @if $position == "top" {
      width: 100%;
      height: $thickness;
      bottom: 100%;
      left: 0;

      background-image: linear-gradient(
        to right,
        clr(accent1),
        clr(accent2),
        clr(accent3)
      );
    }

    @if $position == "left" {
      width: $thickness;
      height: 100%;
      top: 0;
      left: 0;

      background-image: linear-gradient(
        to bottom,
        var(--clr-accent-1),
        var(--clr-accent-2),
        var(--clr-accent-3)
      );
    }
  }
}

sources.scss/base/_reset.scss:
/* Box sizing rules */
*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
  margin: 0;
}

/* Set core root defaults */
html:focus-within {
  scroll-behavior: smooth;
}

/* Set core body defaults */
body {
  min-height: 100vh;
  text-rendering: optimizeSpeed;
  line-height: 1.5;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
  text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
  max-width: 100%;
  display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
  font: inherit;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
    scroll-behavior: auto;
  }

  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

//sr-only
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap; /* added line */
  border: 0;
}

