sources.scss/layout/_four-elements.scss:
@use "../abstracts/mixins" as *;

.four-elements {
  max-width: 2600px;
  margin-inline: auto;

  @include media("small") {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
  }
  @include media("large") {
    grid-template-columns: repeat(4, 1fr);
  }
}

sources.scss/layout/_three-elements.scss:
@use "../abstracts/mixins" as *;

.three-elements {
  margin-inline: auto;

  display: grid;
  grid-template-columns: 1fr;
  @include media("large") {
    grid-template-columns: repeat(3, 1fr);
  }

  &--features {
    padding: 6rem 0;
    column-gap: 3rem;
    row-gap: 5rem;
  }

  &--pricing {
    padding: 6rem 0;
    column-gap: 2rem;
    row-gap: 2rem;

    align-items: center;
    justify-items: stretch;

    .toggle {
      grid-column: 1 / -1;
    }
  }
}

sources.scss/layout/_header.scss:
@use "../abstracts/mixins" as *;

.container--header {
  position: relative;
  z-index: 1000;
}

.primary-header {
  padding: 1.2rem;
  @include media(0px, "medium") {
    &__cta {
      display: none;
    }
    &__nav {
      display: block;

      width: 100vw;
      height: 240px;

      position: absolute;
      left: 0;
      right: 0;
      top: 100%;

      transform: translateY(-100%);
      z-index: -1;
    }

    &__nav[data-visible="true"] {
      transform: translateY(0);
    }
  }

  @include media("medium") {
    &__toggle {
      display: none;
    }
  }
}

sources.scss/layout/_footer.scss:
@use "../abstracts/mixins" as *;

.footer {
  display: grid;
  place-items: center;

  row-gap: 3rem;
  padding-block: 4rem;

  @include media("medium") {
    row-gap: 0;
    grid-template-columns: max-content max-content 1fr max-content;
    grid-template-rows: repeat(4, 1fr);
    grid-template-areas:
      "logo             nav-list      ....      cta      "
      "....             nav-list      ....      ....     "
      "....             nav-list      ....      ....     "
      "social-list      nav-list      ....      fineprint";

    &__logo {
      grid-area: logo;
    }
    &__social-list {
      grid-area: social-list;
    }
    &__nav-list {
      grid-area: nav-list;

      margin-left: 8vw !important;
    }
    &__cta {
      grid-area: cta;
      justify-self: start;
    }
    &__fineprint {
      grid-area: fineprint;
    }
  }
}

sources.scss/layout/_beta.scss:
