.beta {
  &__container {
    @include color-strip("left");
    padding: 3.5rem 0;

    background-image: url("/images/bg-beta-mobile.jpg");

    @include media("small") {
      background-image: url("/images/bg-beta-tablet.jpg");
    }

    @include media("medium") {
      background-image: url("/images/bg-beta.jpg");
    }
